// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";

import "./Adminable.sol";

/// dragon main bone
contract DragonMainBone is
    ERC1155,
    Ownable,
    Adminable,
    Pausable,
    ERC1155Burnable
{
    event Compound(
        address indexed account,
        uint256 id,
        uint256 amount,
        uint256 newid
    );

    event MinterTransferred(
        address indexed prevMinter,
        address indexed newMinter
    );

    uint256 public constant Water1 = 11;
    uint256 public constant Water2 = 12;
    uint256 public constant Water3 = 13;

    uint256 public constant Fire1 = 21;
    uint256 public constant Fire2 = 22;
    uint256 public constant Fire3 = 23;

    uint256 public constant Rock1 = 31;
    uint256 public constant Rock2 = 32;
    uint256 public constant Rock3 = 33;

    uint256 public constant Storm1 = 41;
    uint256 public constant Storm2 = 42;
    uint256 public constant Storm3 = 43;

    uint256 public constant Thunder1 = 51;
    uint256 public constant Thunder2 = 52;
    uint256 public constant Thunder3 = 53;

    uint256 public compound_limit = 3;
    uint256 public compound_amount = 5;
    uint256 public compound_weight = 80;

    constructor() ERC1155("https://dragonmainland.io/storage/item/{id}.json") {
        // _mint(msg.sender, Water1, 10**18, "");
        // _mint(msg.sender, Water2, 10**18, "");
        // _mint(msg.sender, Water3, 10**18, "");
    }

    function setURI(string memory newuri) public onlyOwner {
        _setURI(newuri);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function mint(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public onlyAdmin {
        _mint(account, id, amount, data);
    }

    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public onlyAdmin {
        _mintBatch(to, ids, amounts, data);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override whenNotPaused {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function setCompoundLimit(uint256 _limit) external onlyOwner {
        require(_limit > 0, "invalid compound limit");
        compound_limit = _limit;
    }

    function setCompoundAmount(uint256 _amount) external onlyOwner {
        require(_amount > 0, "invalid compound amount");
        compound_amount = _amount;
    }

    function setCompoundWeight(uint256 _weight) external onlyOwner {
        require(_weight > 0, "invalid compound weight");
        compound_weight = _weight;
    }

    function _random() internal view returns (uint256) {
        uint256 rand = uint256(
            keccak256(
                abi.encodePacked(
                    block.timestamp,
                    block.difficulty,
                    blockhash(block.number),
                    block.coinbase
                )
            )
        );
        return rand % 100 < compound_weight ? 0 : 1;
    }

    // 80%=0 20%=1
    function compound(uint256 _id, uint256 _amount)
        external
        whenNotPaused
        returns (uint256)
    {
        require(_amount == compound_amount, "invalid compound amount");
        require(_id % 10 < compound_limit, "invalid compound limit");
        require(balanceOf(msg.sender, _id) >= _amount, "balance is not enough");
        _burn(msg.sender, _id, _amount);
        uint256 newid = _id + _random();
        _mint(msg.sender, newid, 1, "");
        emit Compound(msg.sender, _id, _amount, newid);
        return newid;
    }

    receive() external payable {}

    function destroy() external onlyOwner {
        selfdestruct(payable(msg.sender));
    }
}

contracts/DragonExchange.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.8.0;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

import "./DragonTransfer.sol";
import "./Adminable.sol";

// IDragonToken
struct Attribute {
    uint256 health;
    uint256 attack;
    uint256 defense;
    uint256 speed;
    uint256 lifeForce;
}

struct Skill {
    uint256 horn;
    uint256 ear;
    uint256 wing;
    uint256 tail;
    uint256 talent;
}

interface IDragonToken {
    function ownerOf(uint256 tokenId) external view returns (address owner);

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) external;

    // create dragon egg
    function newDragonEgg(
        uint8 _job,
        uint256 _id,
        address _owner,
        uint256 _timestamp,
        bytes memory _sign
    ) external returns (bool);

    // grow up dragon
    function growupDragon(
        uint256 _id,
        uint256 _geneDomi,
        uint256 _geneRece,
        uint256 _matronId,
        uint256 _sireId,
        uint16 _stage,
        Attribute memory _attr,
        Skill memory _skill,
        string memory _uri,
        bytes memory _sign
    ) external returns (bool);

    function burn(uint256 tokenId) external;
}

/**
 * dragon contract exchange
 */

/// dragon token 721 exchange
contract DragonExchange is
    Pausable,
    Ownable,
    DragonTransfer,
    ERC721Holder,
    Adminable
{
    // add market
    event AddMarket(
        address indexed from,
        uint256 tokenId,
        uint256 price,
        uint8 exType,
        uint256 minPrice,
        uint256 maxPrice,
        uint256 timeHours
    );

    // remove market
    event RemoveMarket(address indexed from, uint256 tokenId);

    // nft exchange
    event Exchange(
        address indexed from,
        address indexed to,
        uint256 tokenId,
        uint256 price,
        uint8 exType,
        uint256 minPrice,
        uint256 maxPrice
    );

    IDragonToken public dragonToken;

    struct ExchangeData {
        address from;
        address to;
        uint256 price; // price
        uint256 create;
        uint8 ex_type; // 1=exchange 2=auction
        bool state;
        uint256 min_price;
        uint256 max_price;
        uint256 time_hours;
    }

    // nft tokenId => sell price
    mapping(uint256 => ExchangeData) public exchangeDatas;
    // dragon breed count
    mapping(uint256 => uint256) private _breedCounts;
    // dragon breed count max
    uint256 public _breedCountMax = 7;

    function setBreedCountMax(uint256 _count) external onlyOwner {
        _breedCountMax = _count;
    }

    modifier isExchange(uint256 _tokenId) {
        require(exchangeDatas[_tokenId].state == true, "nft state invalid");
        _;
    }

    constructor(address payable _dragon) {
        dragonToken = IDragonToken(_dragon);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    // nft join market
    // _price dms price
    function addMarket(
        uint256 _tokenId,
        uint256 _price,
        uint8 _exType,
        uint256 _minPrice,
        uint256 _maxPrice,
        uint256 _timeHours
    ) external whenNotPaused returns (bool) {
        require(exchangeDatas[_tokenId].state == false, "nft is in market");
        require(
            dragonToken.ownerOf(_tokenId) == msg.sender,
            "nft owner invalid"
        );
        if (_exType == 2) {
            require(_minPrice > 0, "invalid minPrice");
            require(_maxPrice > _minPrice, "invalid maxPrice");
            require(_timeHours >= 24, "invalid timeHours");
        }

        dragonToken.safeTransferFrom(msg.sender, address(this), _tokenId);
        exchangeDatas[_tokenId] = ExchangeData(
            msg.sender,
            address(0),
            _price,
            block.timestamp,
            _exType,
            true,
            _minPrice,
            _maxPrice,
            _timeHours
        );
        emit AddMarket(
            msg.sender,
            _tokenId,
            _price,
            _exType,
            _minPrice,
            _maxPrice,
            _timeHours
        );
        return true;
    }

    // nft remove market
    function removeMarket(uint256 _tokenId)
        external
        whenNotPaused
        isExchange(_tokenId)
        returns (bool)
    {
        require(
            exchangeDatas[_tokenId].from == msg.sender,
            "nft owner invalid"
        );

        ExchangeData storage _exdata = exchangeDatas[_tokenId];
        _exdata.state = false;
        dragonToken.safeTransferFrom(address(this), msg.sender, _tokenId);
        emit RemoveMarket(msg.sender, _tokenId);
        return true;
    }

    // current exchange price
    function exchangePrice(uint256 _tokenId)
        public
        view
        returns (uint256, uint256)
    {
        ExchangeData memory _exdata = exchangeDatas[_tokenId];
        if (_exdata.ex_type == 1) {
            return (_exdata.price, 0);
        } else if (_exdata.ex_type == 2) {
            if (block.timestamp >= _exdata.create + _exdata.time_hours * 3600) {
                return (_exdata.min_price, 0);
            }
            uint256 _hour = (block.timestamp - _exdata.create) / 3600;
            uint256 _one = (_exdata.max_price - _exdata.min_price) /
                _exdata.time_hours;
            uint256 _price = _exdata.max_price - _one * _hour;
            return (_price, (block.timestamp - _exdata.create) % 3600);
        } else {
            return (0, 0);
        }
    }

    // nft exchange
    // transfer dms token
    function exchange(uint256 _tokenId, uint256 _price)
        external
        whenNotPaused
        isExchange(_tokenId)
        returns (bool)
    {
        ExchangeData storage _exdata = exchangeDatas[_tokenId];
        uint256 _balance = dmsToken.balanceOf(msg.sender);
        require(_balance >= _price, "balance is low");

        uint8 _exType = _exdata.ex_type;
        if (_exType != 1 && _exType != 2) {
            return false;
        }
        if (_exType == 1) {
            require(_price >= _exdata.price, "invalid price");
        } else if (_exType == 2) {
            uint256 _currPrice;
            uint256 _currTime;
            (_currPrice, _currTime) = exchangePrice(_tokenId);
            require(_price >= _currPrice, "invalid price");
        }
        uint256 _fee = exchangeFee(_price);
        // dms token
        dmsTransferEarn(msg.sender, _fee);
        dmsTransferFrom(msg.sender, _exdata.from, _price - _fee);
        // dragon nft
        dragonToken.safeTransferFrom(address(this), msg.sender, _tokenId);
        _exdata.to = msg.sender;
        _exdata.state = false;
        return true;
    }

    // breed dragon
    function breedDragon(
        uint8 _job,
        uint256 _tokenId,
        address _owner,
        uint256 _timestamp,
        bytes memory _sign
    ) external whenNotPaused returns (bool) {
        require(_job >= 1 && _job <= 5, "job invalid");
        require(_tokenId > 0, "id invalid");
        require(_breedCounts[_tokenId] < _breedCountMax, "breed count max");
        // dms token
        dmsTransferEarn(msg.sender, breedDmsAmt);
        dmpTransferEarn(msg.sender, breedDmpAmt);
        // dragon token
        require(
            dragonToken.newDragonEgg(_job, _tokenId, _owner, _timestamp, _sign)
        );
        _breedCounts[_tokenId] += 1;

        return true;
    }
}

